package AutomationFramework;

public class JavaLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i < 5; i++) {
		System.out.println(i);	
		//this will iterate the code until the desired condition is met
		//this is for fixed number or iterations
		
		}
		
		int i=0;
		
		while (i<=6) {
			System.out.println(i);
		i++;
		
		}
		
	//	while(true) {
	//		System.err.println(1);
	//	i++;
	//	}
		
		int j=0;
		
		do {
		System.out.println(j);
		}while (j<0);
		// number of iterations not fixed and have to run the loop atleast once
	}

}
